INSERT INTO `articles` VALUES ('11', '2020-10-13', 'article-ប-រជ-ពលរដ-ឋ-ដ-លរងគ-រ-ដ-យជ-នន-ទ-កភ-ល-ង-ន-ខ-ត-តប-ត-ដ-បង-ក-ព-ងត-រ-វប-នជ-យសង-គ-រ-', 'ប្រជាពលរដ្ឋ ដែលរងគ្រោះដោយជំនន់ទឹកភ្លៀង នៅខេត្តបាត់ដំបង កំពុងត្រូវបានជួយសង្គ្រោះ', '<p><span style=\"color: rgb(34, 34, 34); font-family: Battambang, Arial, Helvetica, sans-serif; font-size: 14px;\">នាព្រឹកថ្ងៃទី១២ ខែតុលា ឆ្នាំ២០២០ កម្លាំងអន្តរាគមន៍របស់យោធភូមិភាគទី៥ បានបន្តចុះជួយសង្គ្រោះប្រជាពលរដ្ឋដែលរងគ្រោះដោយសារជំនន់ទឹកភ្លៀងនៅភូមិសូភី១ សូភី២ សូភី៣ ក្រុងបាត់ដំបង ខេត្តបាត់ដំបង ។&nbsp;</span><span style=\"color: rgb(34, 34, 34); font-family: Battambang, Arial, Helvetica, sans-serif; font-size: 14px;\">គិតត្រឹមព្រឹកនេះ ស្ថានភាពទឹកស្ទឹងសង្កែមានកម្ពស់ ១៤ម៉ែត្រ ក្នុងកម្រិតប្រកាសអាសន្នដដែល និងកម្ពស់ទឹកក្នុងភូមិទាំងបីខាងលើគឺ មានកម្រិត ១.២ម៉ែត្រទៅ ១.៥ម៉ែត្រ ហើយស្ថានភាពប្រជាពលរដ្ឋដែលរស់នៅតាមបណ្ដាភូមិសង្កាត់នៅតាមដងស្ទឹងសង្កែ កំពុងតែរងនូវជំនន់ទឹកភ្លៀងដែលហូរចេញពីដងស្ទឹងសង្កែចូលទៅជន់លិចតាមលំនៅដ្ឋានប្រជាពលរដ្ឋ ក្រុងបាត់ដំបង ។</span><br></p>', '0', 'https://www.youtube.com/embed/UaaoBac2TTE', '2020-10-13 07:07:05', '2020-10-13 07:08:51', '1', '1', '1', null);
